mapper.select:
SELECT COALESCE(SUM(CASE WHEN e.direction = 'CREDIT' THEN e.amount ELSE -e.amount END), 0) AS total_income
FROM fin_entries e
INNER JOIN fin_accounts a ON e.account_id = a.id
INNER JOIN fin_transactions t ON e.trans_id = t.id
WHERE a.account_type = 'INCOME'
  AND e.book_id = #{bookId}
  AND t.trans_date >= #{monthStart}
  AND t.trans_date <= #{monthEnd}

SELECT COALESCE(SUM(CASE WHEN e.direction = 'DEBIT' THEN e.amount ELSE -e.amount END), 0) AS total_expense
FROM fin_entries e
INNER JOIN fin_accounts a ON e.account_id = a.id
INNER JOIN fin_transactions t ON e.trans_id = t.id
WHERE a.account_type = 'EXPENSE'
  AND e.book_id = #{bookId}
  AND t.trans_date >= #{monthStart}
  AND t.trans_date <= #{monthEnd}
